        <div className={`px-3 py-1 rounded-full text-sm font-medium ${
          connectionStatus === 'connected' ? 'bg-green-100 text-green-800' :
          connectionStatus === 'connecting' ? 'bg-blue-100 text-blue-800' :
          'bg-gray-100 text-gray-800'
